package staticDemo;

public class OperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		System.out.println(i); //10
		i++;  
		System.out.println(i);  // 11
		++i;
		System.out.println(++i); //
		System.out.println(i++);
		

	}

}
